package leetcode;

import static org.assertj.core.api.Assertions.assertThat;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

public class LC0005_Longest_Palindromic_Substring_Test {
	LC0005_Longest_Palindromic_Substring lc;
	
	@BeforeEach
	void setUp() {
		lc = new LC0005_Longest_Palindromic_Substring();
	}
	
	@Test
	void test() {
		assertThat(lc.longestPalindrome("babad")).isEqualTo("bab");
		assertThat(lc.longestPalindrome("cbbd")).isEqualTo("bb");
		assertThat(lc.longestPalindrome("a")).isEqualTo("a");
		assertThat(lc.longestPalindrome("ac")).isEqualTo("a");
		assertThat(lc.longestPalindrome("bb")).isEqualTo("bb");
		assertThat(lc.longestPalindrome("")).isEqualTo("gykrkyg");
	}
}
